<?php

namespace HIA\FormBundle\Entity;

use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\Tools\Pagination\Paginator;

/**
 * RegistrationRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RegistrationRepository extends EntityRepository
{
    public function getRegistrations($idUser, $idStatus, $who, $offset, $limit)
    {
        $qb = $this->createQueryBuilder('r')
                    ->leftJoin('r.form', 'f')
                    ->leftJoin('r.userSubmit', 'us')
                    ->leftJoin('r.userValidate', 'uv')
                    ->leftJoin('f.readers', 'g')
                    ->leftJoin('g.users', 'u')
                    ->addSelect('f')
                    ->addSelect('us')
                    ->orderBy('r.registrationDate', 'ASC')
                                        
                    ->where('r.status IN (:idStatus)')
                    ->setParameters(array(                        
                        "idUser" => $idUser,
                        "idStatus" => $idStatus
                    ))

                    ->setFirstResult($offset)
                    ->setMaxResults($limit);
                
                    if ($who['submitByUser'] AND !$who['submitByOther']) {
                        $qb->andWhere('us.id = :idUser');
                    } else if (!$who['submitByUser'] AND $who['submitByOther']) {
                        $qb->andWhere('us.id != :idUser AND u.id = :idUser');
                    } else if ($who['validByUser'] AND !$who['validByOther']) {
                        $qb->andWhere('uv.id = :idUser');
                    } else if (!$who['validByUser'] AND $who['validByOther']) {
                        $qb->andWhere('uv.id != :idUser AND u.id = :idUser');
                    } else {
                        $qb->andWhere('u.id = :idUser OR us.id = :idUser');
                    }

        $results = new Paginator($qb, true);

        return $results->getQuery()->getArrayResult();
    }
    
    public function getAll($idUser, $offset, $limit)
    {
        $qb = $this->createQueryBuilder('r')
                    ->leftJoin('r.form', 'f')
                    ->leftJoin('r.userSubmit', 'us')
                    ->leftJoin('f.readers', 'g')
                    ->leftJoin('g.users', 'u')
                    ->addSelect('f')
                    ->addSelect('us')
                    ->orderBy('r.registrationDate', 'ASC')

                    ->where('u.id = :idUser OR us.id = :idUser')

                    ->setParameter("idUser", $idUser)
                    ->setFirstResult($offset)
                    ->setMaxResults($limit);

        $results = new Paginator($qb, true);

        return $results->getQuery()->getResult();
    }
    
    public function getLastFormUsed($idUser, $offset, $limit)
    {
        $qb = $this->createQueryBuilder('r')
                    ->leftJoin('r.form', 'f')
                    ->addSelect('f')
                    ->leftJoin('f.tags', 't')
                    ->addSelect('t')
                    ->orderBy('r.registrationDate', 'DESC')
                    ->where('r.userSubmit = :idUser')
                    ->setParameter("idUser", $idUser)
                    ->groupBy("r.form")
                    ->setFirstResult($offset)
                    ->setMaxResults($limit);

        $results = new Paginator($qb, true);

        return $results->getQuery()->getResult();
    }

    public function getLastSubmit($idUser, $offset, $limit)
    {
        $qb = $this->createQueryBuilder('r')
                    ->orderBy('r.registrationDate', 'ASC')
                    ->where('r.userSubmit = :idUser')
                    ->setParameter("idUser", $idUser)
                    ->setFirstResult($offset)
                    ->setMaxResults($limit);

        $results = new Paginator($qb, true);

        return $results->getQuery()->getResult();
    }

    public function countUnreadSubmit($idUser, $statusPending)
    {
        return $this->countUnreadSubmitByOther($idUser, $statusPending) + $this->countUnreadSubmitByUser($idUser, $statusPending);
    }

    public function countUnreadSubmitByOther($idUser, $statusPending)
    {
        $qb = $this->createQueryBuilder('r')
                    ->select('COUNT(r)')
                    ->leftJoin('r.form', 'f')
                    ->leftJoin('f.readers', 'g')
                    ->leftJoin('g.users', 'u')
                    //->where('r.status = :statusPending AND u.id = :idUser AND r.userSubmit != :idUser')
					->where('r.userValidate is NULL AND u.id = :idUser AND r.userSubmit != :idUser')
                    ->setParameters(array(
  //                      'statusPending' => $statusPending,
                        'idUser'        => $idUser
                    ));

        return $qb->getQuery()->getSingleScalarResult();
    }

    public function countUnreadSubmitByUser($idUser, $statusPending)
    {
        $qb = $this->createQueryBuilder('r')
                    ->select('COUNT(r)')
                    ->leftJoin('r.form', 'f')
                    ->leftJoin('f.writers', 'g')
                    ->leftJoin('g.users', 'u')
                    //->where('r.status = :statusPending AND u.id = :idUser AND r.userSubmit = :idUser')
					->where('r.userValidate is NULL AND u.id = :idUser AND r.userSubmit = :idUser')
                    ->setParameters(array(
//                        'statusPending' => $statusPending,
                        'idUser'        => $idUser
                    ));

        return $qb->getQuery()->getSingleScalarResult();
    }

    // Soumission que vous avez le droit de lire non soumit par vous
    public function listUnreadSubmitByOther($idUser, $statusPending, $offset, $limit)
    {
        $qb = $this->createQueryBuilder('r')
            ->leftJoin('r.form', 'f')
            ->leftJoin('r.userSubmit', 'us')
            ->addSelect('us')
            ->addSelect('f')
            ->leftJoin('f.readers', 'g')
            ->leftJoin('g.users', 'u')
            ->orderBy("r.registrationDate", 'ASC')
            ->where('r.status = :statusPending AND u.id = :idUser AND r.userSubmit != :idUser')
            ->setParameters(array(
                'statusPending' => $statusPending,
                'idUser'        => $idUser
            ))
            ->setFirstResult($offset)
            ->setMaxResults($limit);

        $results = new Paginator($qb, true);

        return $results->getQuery()->getResult();
    }

    // Non traité soumit par vous
    public function listUnreadSubmitByUser($idUser, $statusPending, $offset, $limit)
    {
        $qb = $this->createQueryBuilder('r')
            ->leftJoin('r.form', 'f')
            ->addSelect('f')
            ->orderBy("r.registrationDate", 'DESC')
            ->where('r.status = :statusPending AND r.userSubmit = :idUser')
            ->setParameters(array(
                'statusPending' => $statusPending,
                'idUser'        => $idUser
            ))
            ->setFirstResult($offset)
            ->setMaxResults($limit);

        $results = new Paginator($qb, true);

        return $results->getQuery()->getResult();
    }

    // Soumit par d'autre traité par vous
    public function listReadSubmitByOther($idUser, $statusPending, $offset, $limit)
    {
        $qb = $this->createQueryBuilder('r')
            ->leftJoin('r.form', 'f')
            ->leftJoin('r.userSubmit', 'u')
            ->addSelect('u')
            ->addSelect('f')
            ->orderBy("r.validationDate", 'ASC')
            ->where('r.userValidate = :idUser AND r.status != :statusPending')
            ->setParameters(array(
                'statusPending' => $statusPending,
                'idUser'        => $idUser
            ))
            ->setFirstResult($offset)
            ->setMaxResults($limit);

        $results = new Paginator($qb, true);

        return $results->getQuery()->getResult();
    }

    // Soumit par vous traitée par d'autre
    public function listReadSubmitByUser($idUser, $statusPending, $offset, $limit)
    {
        $qb = $this->createQueryBuilder('r')
            ->leftJoin('r.form', 'f')
            ->addSelect('f')
            ->where('r.status != :statusPending AND r.userSubmit = :idUser')
            ->orderBy("r.validationDate", 'ASC')
            ->setParameters(array(
                'statusPending' => $statusPending,
                'idUser'        => $idUser
            ))
            ->setFirstResult($offset)
            ->setMaxResults($limit);

        $results = new Paginator($qb, true);

        return $results->getQuery()->getResult();
    }

    public function canRead($idUser, $idRegistration)
    {
        $qb = $this->createQueryBuilder('r')
            ->select("COUNT(r)")
            ->leftJoin("r.form", 'f')
            ->leftJoin("f.readers", 'g')
            ->leftJoin("g.users", 'u')
            ->where("r.id = :idRegistration AND (r.userValidate = :idUser OR r.userSubmit = :idUser OR u.id = :idUser)")
            ->setParameters(array(
                "idUser" => $idUser,
                "idRegistration" => $idRegistration
            ));

        return $qb->getQuery()->getSingleScalarResult();
    }

    public function countSubmitForm($idUser)
    {
        $qb = $this->createQueryBuilder('r')
                ->select("COUNT(r)")
                ->where("r.userSubmit = :idUser")
                ->setParameters(array(
                    "idUser" => $idUser
                ));

        return $qb->getQuery()->getSingleScalarResult();
    }

    public function countValidForm($idUser)
    {
        $qb = $this->createQueryBuilder('r')
                ->select("COUNT(r)")
                ->where("r.userValidate = :idUser")
                ->setParameters(array(
                    "idUser" => $idUser
                ));

        return $qb->getQuery()->getSingleScalarResult();
    }

    public function countRegistrationUserCanAccess($idUser)
    {
        $qb = $this->createQueryBuilder('r')
                ->select('COUNT(r)')
                ->leftJoin('r.form', 'f')
                ->leftJoin('r.userSubmit', 'us')
                ->leftJoin('f.readers', 'g')
                ->leftJoin('g.users', 'u')

                ->where('u.id = :idUser OR us.id = :idUser')

                ->setParameter("idUser", $idUser);

        return $qb->getQuery()->getSingleScalarResult();
    }

    public function countRegistrationUserCanAccessAjax($idUser, $idStatus, $who)
    {
        $qb = $this->createQueryBuilder('r')
            ->select('COUNT(r)')
            ->leftJoin('r.form', 'f')
            ->leftJoin('r.userSubmit', 'us')
            ->leftJoin('r.userValidate', 'uv')
            ->leftJoin('f.readers', 'g')
            ->leftJoin('g.users', 'u')
            ->orderBy('r.registrationDate', 'ASC')

            ->where('r.status IN (:idStatus)')
            ->setParameters(array(
                "idUser" => $idUser,
                "idStatus" => $idStatus
            ));

        if ($who['submitByUser'] AND !$who['submitByOther']) {
            $qb->andWhere('us.id = :idUser');
        } else if (!$who['submitByUser'] AND $who['submitByOther']) {
            $qb->andWhere('us.id != :idUser AND u.id = :idUser');
        } else if ($who['validByUser'] AND !$who['validByOther']) {
            $qb->andWhere('uv.id = :idUser');
        } else if (!$who['validByUser'] AND $who['validByOther']) {
            $qb->andWhere('uv.id != :idUser AND u.id = :idUser');
        } else {
            $qb->andWhere('u.id = :idUser OR us.id = :idUser');
        }

        return $qb->getQuery()->getSingleScalarResult();
    }
}













